//this project is used soley for testing individual tools or packages in isolation from a larger prject
//^^^^
//^^^^
//^^^
//Not a project for anything other than testing

import { useRef, useState } from "react";

import { signup, login, logout, useAuth } from "./firebase";
import Profile from "./Profile";

export default function App() {
  const [ loading, setLoading ] = useState(false);
  const currentUser = useAuth();

  const emailRef = useRef();
  const passwordRef = useRef();

  async function handleSignup() {
    setLoading(true);
    try {
      await signup(emailRef.current.value, passwordRef.current.value);
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  async function handleLogin() {
    setLoading(true);
    try {
      await login(emailRef.current.value, passwordRef.current.value);
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  async function handleLogout() {
    setLoading(true);
    try {
      await logout();
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  return (
    <div id="main">

      <div>Currently logged in as: { currentUser?.email } </div>

      {!currentUser && 
        <>
          <div className="fields">
            <input ref={emailRef} placeholder="Email" />
            <input ref={passwordRef} type="password" placeholder="Password" />
          </div>

          <button disabled={ loading } onClick={handleSignup}>Sign Up</button>
          <button disabled={ loading } onClick={handleLogin}>Log In</button>
        </>
      }

      {currentUser && 
        <>
          <Profile />
          <button disabled={ loading || !currentUser } onClick={handleLogout}>Log Out</button>
        </>
      }

    </div>
  );
}
