function Modal(props) {
  function cancelHandler() {
    props.onCancel();
  }

  function confirmHandler() {
    props.onCancel();
  }

  return (
    <div className="modal">
      <p>Are you sure?</p>
      <button onClick={confirmHandler} className="btn btn--alt">
        Confirm
      </button>
      <button onClick={cancelHandler} className="btn">
        Canvel
      </button>
    </div>
  );
}

export default Modal;
